sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }
		var whtneeded = g_form.getValue('u_wht_needed');

	g_form.clearOptions('u_wht_needed');

	if(newValue == 'hr'){
		g_form.addOption('u_wht_needed', 'hr1', 'Human resources 1');
		g_form.addOption('u_wht_needed', 'hr2', 'Human resources 2');
		g_form.addOption('u_wht_needed', 'other', 'Other');

	}
   if(newValue == 'facilities'){
		g_form.addOption('u_wht_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_wht_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_wht_needed', 'other', 'Other');

	}
	   if(newValue == 'legal'){
		g_form.addOption('u_wht_needed', 'legal1', 'Legal 1');
		g_form.addOption('u_wht_needed', 'legal2', 'Legal 2');
		g_form.addOption('u_wht_needed', 'other', 'Other');
	}
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_wht_needed', whtneeded);
	}
}
